package com.program;

import java.util.Scanner;

public class DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("printing numbers from 1 to 10");
		int i=0;
		do {
			System.out.println("values are "+i);
			i++;
		}
		while(i<=10);
		
	
	

	}

}
